<?php/** Include PHPExcel */require_once dirname(__FILE__) . '/php/PHPExcel/Classes/PHPExcel.php';// Create new PHPExcel object$objPHPExcel = new PHPExcel();// Set document properties$objPHPExcel->getProperties()->setCreator("CMS")    ->setLastModifiedBy("")    ->setTitle("Export from CMS")    ->setSubject("Office 2007 XLSX Test Document")    ->setDescription("")    ->setKeywords("")    ->setCategory("");$qCols=MySQL_Query("SELECT * FROM flags WHERE tableName='".mysql_real_escape_string($_GET[table])."' ORDER BY sequence");while($rCols=MySQL_Fetch_Array($qCols)){    $cols[]=$rCols["colDbName"];    $headerCols[]=$rCols["colWebName"];}//hlavicka excelu$c=0;foreach($headerCols AS $headerCol){    $range=range("A","Z");    $cell=$range[$c++]."1";    $objPHPExcel->setActiveSheetIndex(0)->setCellValue($cell, $headerCol);}$objPHPExcel    ->getActiveSheet()    ->getStyle('A1:Z1')    ->getFill()    ->setFillType(PHPExcel_Style_Fill::FILL_SOLID)    ->getStartColor()    ->setRGB('dddddd');//samotna data excelu$q=MySQL_Query($exportQuery);$i=1;while($r=MySQL_Fetch_Array($q)){    $range=range("A","Z");    $c=0;    $i++;    foreach($cols AS $col){        $cell=$range[$c++].$i;        $objPHPExcel->setActiveSheetIndex(0)->setCellValue($cell, $r[$col]);    }}$objWriter = PHPExcel_IOFactory::createWriter($objPHPExcel, 'Excel2007');$objWriter->save(str_replace('.php', '.xlsx', __FILE__));echo "<p>&raquo; <a href='export.xlsx' target='_blank'>Export dat do Excelu</a></p><br>";exit;